import React from "react";
import axios from "axios";
import { withRouter } from "next/router";
import { FiltersComponent } from "../components/filter";
import "../Resources/main.css";
import { fetchSpacex } from "../Api";

class Spacex extends React.Component {
  constructor(props) {
    super(props);
    this.state = { data: props.data, error: props.error };
  }

  handleApiCall(params) {
    fetchSpacex(params).then((json) => {
      this.setState({ data: json });
    });
  }
  static async getInitialProps() {
    try {
      const res = this.handleApiCall(query);
      return { data: res.data };
    } catch (e) {
      return { error: e };
    }
  }
  componentDidUpdate(prevProps) {
    const { query } = this.props.router;
    // verify props have changed to avoid an infinite loop
    if (query !== prevProps.router.query) {
      // fetch data based on the new query
      this.handleApiCall(query);
    }
  }
  render() {
    console.log("44444444444", this.props);
    const { history } = this.props;
    console.log("history", history);
    if (this.state.error) {
      return <div>Error: {this.props.error.message}</div>;
    } else {
      return (
        <div>
          <h1>Spacex</h1>
          <br />
          <FiltersComponent history={history} />
          {this.state.data.map((item, index) => (
            <div key={index} className="spacex-card">
              <img src={item.links.mission_patch_small} alt="User Icon"></img>
              <div className="UserDetails">
                <div className="card_content">
                  <a href="#">
                    {item.mission_name} #{item.flight_number}
                  </a>
                  {item.mission_id.length && (
                    <ul>
                      <b>Mission Ids</b>
                      {item.mission_id.map((id) => (
                        <li key={id}>{id}</li>
                      ))}
                    </ul>
                  )}
                  {item.launch_year && (
                    <div>
                      <b>Launch Year:</b>
                      {item.launch_year}
                    </div>
                  )}
                  <p>
                    <b>Successful Launch:-</b>
                    {item.launch_success ? "True" : "false"}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      );
    }
  }
}

export default withRouter(Spacex);
