import React from "react";
import axios from "axios";
import { FiltersComponent } from "../components/filter";
import "../Resources/main.css";
import { useRouter } from "next/router";

export default class Spacex extends React.Component {
  static async getInitialProps({ query }) {
    console.log("333333333", query);
    try {
      const res = await axios.get(
        "https://api.spacexdata.com/v3/launches?limit=100"
      );
      console.log("44444444444", res);
      return { data: res.data };
    } catch (e) {
      return { error: e };
    }
  }
  componentDidUpdate(prevProps) {
    console.log("prevProps", prevProps);
    const router = useRouter();
    console.log(router.query, "66666");
    if (this.props.location.search !== prevProps.location.search) {
      this.handleApiCall(this.props.location.search);
    }
  }
  render() {
    console.log("44444444444", this.props);
    const { history } = this.props;
    console.log("history", history);
    if (this.props.error) {
      return <div>Error: {this.props.error.message}</div>;
    } else {
      return (
        <div>
          <h1>Spacex</h1>
          <br />
          <FiltersComponent history={history} />
          {this.props.data.map((item, index) => (
            <div key={index} className="spacex-card">
              <img src={item.links.mission_patch_small} alt="User Icon"></img>
              <div className="UserDetails">
                <div className="card_content">
                  <a href="#">
                    {item.mission_name} #{item.flight_number}
                  </a>
                  {item.mission_id.length && (
                    <ul>
                      <b>Mission Ids</b>
                      {item.mission_id.map((id) => (
                        <li key={id}>{id}</li>
                      ))}
                    </ul>
                  )}
                  {item.launch_year && (
                    <div>
                      <b>Launch Year:</b>
                      {item.launch_year}
                    </div>
                  )}
                  <p>
                    <b>Successful Launch:-</b>
                    {item.launch_success ? "True" : "false"}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      );
    }
  }
}
