import React, { useEffect, useState } from "react";
import axios from "axios";
import { withRouter } from "next/router";
import { FiltersComponent } from "../components/filter";
import "../Resources/main.css";
import { fetchSpacex } from "../Api";

function Spacex(props) {
  const [data, setData] = useState(props.data);
  const { query } = props.router;

  const handleApiCall = (params) => {
    fetchSpacex(params).then((json) => {
      setData(json);
    });
  };

  useEffect(() => {
    // verify props have changed to avoid an infinite loop
    // fetch data based on the new query
    handleApiCall(query);
  }, []);
  const { history } = props;
  //   console.log("1111111111", data);
  if (!data) {
    return <div>Error: {this.props.error.message}</div>;
  } else {
    return (
      <div>
        <h1>Spacex</h1>
        <br />
        <FiltersComponent history={history} />
        {data.map((item, index) => (
          <div key={index} className="spacex-card">
            <img src={item.links.mission_patch_small} alt="User Icon"></img>
            <div className="UserDetails">
              <div className="card_content">
                <a href="#">
                  {item.mission_name} #{item.flight_number}
                </a>
                {item.mission_id.length && (
                  <ul>
                    <b>Mission Ids</b>
                    {item.mission_id.map((id) => (
                      <li key={id}>{id}</li>
                    ))}
                  </ul>
                )}
                {item.launch_year && (
                  <div>
                    <b>Launch Year:</b>
                    {item.launch_year}
                  </div>
                )}
                <p>
                  <b>Successful Launch:-</b>
                  {item.launch_success ? "True" : "false"}
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  }
  // }
}
Spacex.getInitialProps = async (ctx) => {
  console.log("ctx", ctx);
  try {
    var url = new URL("https://api.spacexdata.com/v3/launches?limit=100");

    var params = ctx.query; // or:

    url.search = new URLSearchParams(params).toString();
    const res = await fetch(url);
    return { data: res.data };
  } catch (e) {
    return { error: e };
  }
};
export default withRouter(Spacex);
